  geom_line()+
  geom_ribbon(alpha=.3)

pltn <- effect(term = "N", mod = m1, xlevels = 20) |>
  as.data.frame() |>
  ggplot(aes(x=N,y=fit,ymin=lower,ymax=upper))+
  geom_line()+
  geom_ribbon(alpha=.3)

plta <- effect(term = "A", mod = m1, xlevels = 20) |>
  as.data.frame() |>
  ggplot(aes(x=A,y=fit,ymin=lower,ymax=upper))+
  geom_line()+
  geom_ribbon(alpha=.3)

(plte + plta) / (pltn + pltc)
```

